import {
    LOGIN_REQUEST,
    LOGIN_FAIL,
    LOGIN_SUCCESS,
    CLEAR_ERRORS,
    REGISTER_USER_FAIL,
    REGISTER_USER_SUCCESS,
    REGISTER_USER_REQUEST,
    LOAD_USER_REQUEST,
    LOAD_USER_FAIL,
    LOAD_USER_SUCCESS,
    LOGOUT_SUCCESS,
    LOGOUT_FAIL,
    UPDATE_PASSWD_FAIL,
    UPDATE_PASSWD_REQUEST,
    UPDATE_PASSWD_SUCCESS,
    UPDATE_PASSWD_RESET,
    UPDATE_PROFILE_REQUEST,
    UPDATE_PROFILE_FAIL,
    UPDATE_PROFILE_SUCCESS,
    UPDATE_PROFILE_RESET,
    FORGOT_PASSWD_FAIL,
    FORGOT_PASSWD_SUCCESS,
    FORGOT_PASSWD_REQUEST,
    NEW_PASSWD_SUCCESS,
    NEW_PASSWD_FAIL,
    NEW_PASSWD_REQUEST, ALL_USERS_FAIL, ALL_USERS_SUCCESS, ALL_USERS_REQUEST
} from "../constant/userConstants";

export const authReducer = (state = {user:{}}, action) =>{
    switch (action.type)
    {
        case LOGIN_REQUEST:
        case REGISTER_USER_REQUEST:
        case LOAD_USER_REQUEST:
            return{
                loading:true,
                isAuthenticated:false,
            }

        case LOGIN_SUCCESS:
        case REGISTER_USER_SUCCESS:
        case LOAD_USER_SUCCESS:
                return {

                    ...state,
                    loading: false,
                    isAuthenticated: true,
                    user: action.payload

            }

        case LOAD_USER_FAIL:
            return {
                loading: false,
                isAuthenticated: false,
                user:null,
                error: action.payload

            }
        case LOGOUT_SUCCESS:
            return{
                loading: false,
                isAuthenticated: false,
                user:null
            }


        case LOGOUT_FAIL:
            return {
                ...state,
                error: action.payload
            }

        case LOGIN_FAIL:
        case REGISTER_USER_FAIL:
            return {
                ...state,
                loading: false,
                isAuthenticated: false,
                user: null,
                error:action.payload
            }


        case CLEAR_ERRORS:
            return {
                ...state,
                error:null,
            }
        default:return state;



    }

}

export const userReducer = (state ={},action) =>{

    switch (action.type)
    {

        case UPDATE_PROFILE_REQUEST:
        case UPDATE_PASSWD_REQUEST:
        return {
            ...state,
            loading:true,


        }


        case UPDATE_PROFILE_SUCCESS:
        case UPDATE_PASSWD_SUCCESS:
            return {
            ...state,
                loading:false,
                isUpdated:action.payload

            }


        case UPDATE_PROFILE_FAIL:
        case UPDATE_PASSWD_FAIL:

            return {
                ...state,
                loading:false,
                error:action.payload


            }

        case UPDATE_PROFILE_RESET:
        case UPDATE_PASSWD_RESET:
            return{
                ...state,
                isUpdated: false
            }


        case CLEAR_ERRORS:
            return {
                ...state,
                error:null,
            }




            default: return state;







    }


}

export const forgotPasswordReducer = (state ={},action) =>{

    switch (action.type)
    {

        case FORGOT_PASSWD_REQUEST:
        case NEW_PASSWD_REQUEST:
            return {
                ...state,
                error: null

            }


        case FORGOT_PASSWD_SUCCESS:
            return {
                ...state,
                loading:false,
                message: action.payload

            }

        case NEW_PASSWD_SUCCESS:
            return {
                ...state,
                success:action.payload,
            }


        case FORGOT_PASSWD_FAIL:
        case NEW_PASSWD_FAIL:

            return {
                ...state,
                loading:false,
                error:action.payload


            }



        case CLEAR_ERRORS:
            return {
                ...state,
                error:null,
            }




        default: return state;







    }


}

export const allUsersReducer = (state = { users: [] }, action) => {
    switch (action.type) {

        case ALL_USERS_REQUEST:
            return {
                ...state,
                loading: true,
            }

        case ALL_USERS_SUCCESS:
            return {
                ...state,
                loading: false,
                users: action.payload
            }

        case ALL_USERS_FAIL:
            return {
                ...state,
                loading: false,
                error: action.payload
            }

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null
            }

        default:
            return state;
    }
}
