import React, {Fragment,useState,useEffect} from "react";
import '../../App.css';
import {useAlert} from "react-alert";
import {useDispatch,useSelector} from "react-redux";
import Loader from "../layouts/Loader";
import MetaData from "../layouts/MetaData";
import {Link} from "react-router-dom";
import {register, clearErrors} from "../../actions/userAction";

const Register = ({history}) => {
    const [user,setUser] = useState({
        name:'',
        email:'',
        password:'',
    })
    const {name,email,password} = user;
    const alert = useAlert();
    const dispatch = useDispatch();


    const {isAuthenticated,error,loading} = useSelector(state => state.auth);



    useEffect(()=> {

        if(isAuthenticated){

            history.push('/');
        }

        if(error){
            alert.error(error);
            dispatch(clearErrors());
        }

    },[dispatch, alert, isAuthenticated, error, history])

    const submitHandler = (e) =>{
        e.preventDefault();
        const formData = new FormData();
        formData.set('name',name);
        formData.set('email',email);
        formData.set('password',password);

        dispatch(register(formData));

    }

    const onChange = (e) => {
        setUser({...user,[e.target.name]:e.target.value})


    }



    return(
        <Fragment>
            <MetaData title={'Rejestracja użytkownika'}/>
            <div className="row wrapper">
                <div className="col-10 col-lg-5">
                    <form className="shadow-lg" onSubmit={submitHandler} encType='multipart/form-data'>
                        <h1 className="mb-3">Rejestracja</h1>

                        <div className="form-group">
                            <label htmlFor="email_field">Imię</label>
                            <input
                                type="name"
                                id="name_field"
                                className="form-control"
                                name='name'
                                value={name}
                                onChange={onChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="email_field">Email</label>
                            <input
                                type="email"
                                id="email_field"
                                className="form-control"
                                name='email'
                                value={email}
                                onChange={onChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="password_field">Hasło</label>
                            <h6>Hasło musi składać się z minimum 8 znaków,jednej dużej litery, jednej małej, cyfry oraz znaku specjalnego.</h6>
                            <input
                                type="password"
                                id="password_field"
                                className="form-control"
                                name='password'
                                value={password}
                                onChange={onChange}
                            />
                        </div>
                        <button
                            id="register_button"
                            type="submit"
                            className="btn btn-block py-3"
                            disabled={loading? true : false}
                        >
                            Zarejestruj
                        </button>
                    </form>
                </div>
            </div>


        </Fragment>
    )


}

export default Register;
