import {createStore,combineReducers,applyMiddleware} from "redux";
import thunk from "redux-thunk";
import {composeWithDevTools} from "redux-devtools-extension";
import {productReducer,productsReducer,productDetailsReducer,newReviewReducer,newProductReducer} from "./reducer/productReducer";
import {authReducer, userReducer, forgotPasswordReducer} from "./reducer/userReducer";
import {cartReducer} from "./reducer/cartReducer";
import {newOrderReducer,myOrdersReducer,orderDetailsReducer} from "./reducer/orderReducer";



const reducer = combineReducers({
    products:productReducer,
    productDetails:productDetailsReducer,
    auth:authReducer,
    user:userReducer,
    forgotPassword:forgotPasswordReducer,
    cart: cartReducer,
    newOrder:newOrderReducer,
    myOrders:myOrdersReducer,
    orderDetails:orderDetailsReducer,
    newReview:newReviewReducer,
    newProduct:newProductReducer,

})

let initialState = {}

const middleware = [thunk];

const store = createStore(reducer,initialState,composeWithDevTools(applyMiddleware(...middleware)));

export default store;
