import axios from "axios";

import {ALL_PRODUCT_REQUEST,ALL_PRODUCT_SUCCESS,ALL_PRODUCT_FAIL,CLEAR_ERRORS,PRODUCT_DETAILS_FAIL,PRODUCT_DETAILS_SUCCESS,PRODUCT_DETAILS_REQUEST} from '../constant/ProductConstant';

export const getProducts = (keyword='',price,category) => async (dispatch) =>{
    try{
    dispatch({type: ALL_PRODUCT_REQUEST})
        let link = `/api/v1/products?keyword=${keyword}&price[lte]=${price[1]}&price[gte]=${price[0]}`

        if (category){
            link = `/api/v1/products?keyword=${keyword}&price[lte]=${price[1]}&price[gte]=${price[0]}&`
        }

        const {data} = await axios.get(link)
        dispatch({
            type:ALL_PRODUCT_SUCCESS,
            payload:data,
        })
    }
    catch (err)
    {
        dispatch({
            type:ALL_PRODUCT_FAIL,
            payload:err.response.data.message
        })
    }
}



export const getDetailsProducts = (id) => async (dispatch) => {
    try {

        dispatch({ type: PRODUCT_DETAILS_REQUEST })

        const { data } = await axios.get(`/api/v1/product/${id}`)

        dispatch({
            type: PRODUCT_DETAILS_SUCCESS,
            payload: data.product
        })

    } catch (error) {
        dispatch({
            type: PRODUCT_DETAILS_FAIL,
            payload: error.response.data.message
        })
    }
}
//clear errors

export const clearErrors = () => async (dispatch) =>{
    dispatch({
        type:CLEAR_ERRORS
    })
}
