import React,{Fragment} from "react";
import {Link} from "react-router-dom";
import {useSelector} from "react-redux";
import MetaData from "../layouts/MetaData";
import Loader from "../layouts/Loader";
const Profile = () =>{

    const {user,loading} = useSelector(state=> state.auth)

    return (
        <Fragment>
            {loading ? <Loader/> :(
                <Fragment>
                <MetaData title={"Profil użytkownika"}/>
                    <h2 className="mt-2 ml-8">My Profile</h2>
                    <div className="row justify-content-around mt-5 user-info">


                        <div className="col-12 col-md-5">
                            <h4>Full Name</h4>
                            <p>{user.name}</p>

                            <h4>Email Address</h4>
                            <p>{user.email}</p>

                            <h4>Joined in</h4>
                            <p>{String(user.createdAt).substring(0,10)}</p>

                            {user.role ==='admin' && (
                            <Link to="/orders/me" className="btn btn-danger btn-block mt-5">
                                My Orders
                            </Link>
                            )}
                            <Link href="/password/update" className="btn btn-primary btn-block mt-3">
                                Change Password
                            </Link>
                            <Link to="/me/update" id="edit_profile" className="btn btn-primary btn-block mt-3">
                                Edit Profile
                            </Link>

                        </div>
                    </div>


                </Fragment>

            )}



        </Fragment>
    )



}


export default Profile
