import React, {Fragment,useState,useEffect} from "react";
import '../../App.css';
import {useAlert} from "react-alert";
import {useDispatch,useSelector} from "react-redux";
import MetaData from "../layouts/MetaData";
import {forgotPassword, clearErrors, updateProfile, loadUser, updatePassword} from "../../actions/userAction";
import {UPDATE_PASSWD_RESET, UPDATE_PROFILE_RESET} from "../../constant/userConstants";


const ForgotPassword = () =>{
    const [email, setEmail] = useState('');

    const alert = useAlert();
    const dispatch = useDispatch();


    const {error, message,loading} = useSelector(state => state.forgotPassword);


    useEffect(() => {


        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }
        if (message) {
            alert.success('token został wysłany');
            dispatch(loadUser());
            history.push('/me')

            dispatch({
                type: UPDATE_PASSWD_RESET
            })
        }

    }, [dispatch, alert, error,message])

    const submitHandler = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.set('email', email);

        dispatch(forgotPassword(formData));
    }


    return(
        <Fragment>
        <MetaData title={'forgot password'}/>

            <div className="row wrapper">
                <div className="col-10 col-lg-5">
                    <form className="shadow-lg" onClick={submitHandler}>
                        <h1 className="mb-3">Forgot Password</h1>
                        <div className="form-group">
                            <label htmlFor="email_field">Enter Email</label>
                            <input
                                type="email"
                                id="email_field"
                                className="form-control"
                                value={email}
                                onChange={(e)=>setEmail(e.target.value)}

                            />
                        </div>

                        <button
                            id="forgot_password_button"
                            type="submit"
                            className="btn btn-block py-3" disabled={loading ? true:false}>
                            Send Email
                        </button>

                    </form>
                </div>
            </div>



        </Fragment>
    )



}

export default ForgotPassword;
